import java.io.IOException;import java.net.ServerSocket;/** * Created by Bernd on 12.11.2015. */public class ConnectionListener implements Runnable {    public final static int PORT = 8080;    final ServerSocket serverSocket;    public ConnectionListener() throws IOException {        this.serverSocket = new ServerSocket(PORT);    }    @Override    public void run() {        while (!Thread.currentThread().isInterrupted()) {            try {                new Thread(new SocketHandler(serverSocket.accept())).start();            } catch (IOException e) {                e.printStackTrace();            }        }    }}